import React, { useState } from 'react'
import usePokemon from '../hooks/usePokemon'

export default function CardContainer(props) {
  const { pokemon } = usePokemon(props.url)
  const [resultados, setResultados] = usePokemon()
  setResultados(pokemon)

  return (
    <>
      <div className='card' onClick={() => window.open(props.url, '_blank')} style={{
        width: 300,
        height: 400,
        background: props.color,
        textDecoration: 'none',
        color: 'black'
      }}>
        <h2>{resultados.forms[0].name}</h2>
        <img className='card_img' src={resultados.sprites.front_default}></img>
        <p className='card_type'>{resultados.types[0].type.name}</p>
      </div>
    </>
  )
}
