import React, { useState } from 'react'
import Button from 'react-bootstrap/Button'
import Card from 'react-bootstrap/Card'

const box = {
  width: '300px',
  height: '300px',
  background: 'blue'
}

export default function DeactivateBox() {
  const [deactivate, setDeactivate] = useState(true)

  const button = () => {
    const [isLoggedIn, setIsLoggedIn] = useState(false);

    if (!isLoggedIn) return <SignIn handleEvent={setIsLoggedIn} />;

    return <Logout handleEvent={setIsLoggedIn} />;
  }

  return (
    <>
      <Card>
        <Card.Header>Featured</Card.Header>
        <Card.Body>
          <div style={{
            display: 'flex',
            justifyContent: 'space-around',
            paddingBottom: 30,
          }}>
            <div style={box} />
            <div style={box} />
            <div style={box, { visibility: validate === true ? 'visible' : 'hidden' }} />
          </div>
          <Button variant="primary" onClick={() => this.handleClick(validate)}>Click</Button>
        </Card.Body>
      </Card>
    </>
  )
}
