import React, { useState } from 'react'
import Button from 'react-bootstrap/Button'
import Card from 'react-bootstrap/Card'
import Form from 'react-bootstrap/Form'

const box = {
  width: '300px',
  height: '300px',
  background: 'blue'
}

export default function DeactivateBox() {
  const [status, setStatus] = useState(true)
  let activator = ''

  if (status) {
    activator = 'visible'
  } else {
    activator = 'hidden'
  }

  return (
    <>
      <Card>
        <Card.Header>Featured</Card.Header>
        <Card.Body>
          <div style={{
            display: 'flex',
            justifyContent: 'space-around',
            paddingBottom: 30,
          }}>
            <div style={box} />
            <div style={box} />
            <div style={{
              width: '300px',
              height: '300px',
              background: 'blue',
              visibility: activator,
            }} />
          </div>
          <Form style={{
            display: 'flex',
            justifyContent: 'space-around'
          }}>
            <Form.Control type="email" placeholder="Number" style={{ width: '300px', }} />
            <Button variant="primary" onClick={e => setStatus(!status)}>Click</Button>
          </Form>
        </Card.Body>
      </Card>
    </>
  )
}
