import React, { useState } from 'react'
import Button from 'react-bootstrap/Button'
import Card from 'react-bootstrap/Card'

const box = {
  width: '300px',
  height: '300px',
  background: 'blue'
}

function Click() {
  const [validate, setValidate] = useState(false)

  if (!validate) return <Button variant="primary" onClick={setValidate(false)}>Desactivar</Button>

  return <Button variant="primary" onClick={setValidate(true)}>Activar</Button>
}

export default function DeactivateBox() {
  return (
    <>
      <Card>
        <Card.Header>Featured</Card.Header>
        <Card.Body>
          <div style={{
            display: 'flex',
            justifyContent: 'space-around',
            paddingBottom: 30,
          }}>
            <div style={box} />
            <div style={box} />
            <div style={box} />
          </div>
          <Click />
        </Card.Body>
      </Card>
    </>
  )
}
