import React, { useState } from 'react'
import Button from 'react-bootstrap/Button'
import Card from 'react-bootstrap/Card'
import Form from 'react-bootstrap/Form'

export default function DeactivateBox() {
  let numberBox = 0

  const [status, setStatus] = useState(true)
  let activator1 = ''
  let activator2 = ''
  let activator3 = ''

  if (status) {
    //activator = 'visible'
  } else {
    //activator = 'hidden'
  }

  return (
    <>
      <Card>
        <Card.Header>Featured</Card.Header>
        <Card.Body>
          <div style={{
            display: 'flex',
            justifyContent: 'space-around',
            paddingBottom: 30,
          }}>
            <div style={{
              width: '300px',
              height: '300px',
              background: 'blue',
              visibility: activator1,
            }} />
            <div style={{
              width: '300px',
              height: '300px',
              background: 'blue',
              visibility: activator2,
            }} />
            <div style={{
              width: '300px',
              height: '300px',
              background: 'blue',
              visibility: activator3,
            }} />
          </div>

          <Form style={{
            display: 'flex',
            justifyContent: 'center',
          }}>
            <Form.Group
              onSubmit={ev => {
                ev.preventDefault()
                numberBox = ev.target.number.value
                console.log(ev.target.number.value)
              }}
              style={{
                display: 'flex',
                justifyContent: 'space-around',
                width: '400px',
              }}
            >
              <Form.Control type='text' name='number' placeholder="Number" style={{ width: '300px', }} />
              <Button variant="primary" type="submit">Click</Button>
            </Form.Group>
          </Form>
        </Card.Body>
      </Card>
    </>
  )
}

