import React, { useState } from 'react'
import Button from 'react-bootstrap/Button'
import Card from 'react-bootstrap/Card'
import Form from 'react-bootstrap/Form'

export default function DeactivateBox() {
  const [number, setNumber] = useState('')
  console.log(numberBox)

  const [status1, setStatus1] = useState('visible')
  const [status2, setStatus2] = useState('visible')
  const [status3, setStatus3] = useState('visible')

  switch (numberBox) {
    case "1":
      if (status1 === 'visible') {
        setStatus1('hidden')
      } else {
        setStatus1('visible')
      }
      break
    case "2":
      if (status2 === 'visible') {
        setStatus2('hidden')
      } else {
        setStatus1('visible')
      }
      break
    case "3":
      if (status3 === 'visible') {
        setStatus3('hidden')
      } else {
        setStatus1('visible')
      }
      break
    default:
      console.log('No hay')
  }

  return (
    <>
      <Card>
        <Card.Header>Featured</Card.Header>
        <Card.Body>
          <div style={{
            display: 'flex',
            justifyContent: 'space-around',
            paddingBottom: 30,
          }}>
            <div style={{
              width: '300px',
              height: '300px',
              background: 'blue',
              visibility: status1,
            }} />
            <div style={{
              width: '300px',
              height: '300px',
              background: 'blue',
              visibility: status2,
            }} />
            <div style={{
              width: '300px',
              height: '300px',
              background: 'blue',
              visibility: status3,
            }} />
          </div>

          <Form
            onSubmit={ev => {
              ev.preventDefault()
              setNumber(ev.target.number.value)
            }}
            style={{
              display: 'flex',
              justifyContent: 'center',
            }}
          >
            <Form.Group
              style={{
                display: 'flex',
                justifyContent: 'space-around',
                width: '400px',
              }}
            >
              <Form.Control type='text' name='number' placeholder="Number" style={{ width: '300px', }} />
              <Button variant="primary" type="submit">Click</Button>
            </Form.Group>
          </Form>
        </Card.Body>
      </Card>
    </>
  )
}

